	get() {
		Dep.target = this;
		let value = this.getter(vm，expOrFn,cb); // 会触发数据的getter 方法， 可以先不关心它具体做了啥
		Dep.target = null;
		return value;
	}

当解析到UI里用到了某一个数据后，我们就new Watcher(vm，expOrFn,cb)得到一个watcher实例, Watcher的constructor 会执行其get方法，该方法里先将Dep.target赋值为当前watcher实例，接着去访问这个数据，就会触发这个数据的getter方法，在数据的getter 里我们调用dep.deepend方法，deepend方法内将Dep.target（此时就是watcher实例）添加到当前数据的订阅者队列。 然后再将Dep.target置空。

我们再回顾下前面的定义响应式的方法对每个数据定义的getter：


	function defineReactive(data, key, val) {
		let dep = new Dep(); //闭包变量
	    Object.defineProperty(obj, key, {
	        get() {
	        		dep.depend(); //添加订阅者
	          	return val;
	        },
	        set(newVal) {
	          	//....
	        }
	    })
	}

这就保证了只有通过new Watcher去访问的数据才会被添加到该数据的订阅者队列。
比如我们在代码里写 `data.a` 这样去访问属性a时，由于Dep.target是null,所以不会被添加无意义的订阅者。

至此数据绑定原理基本上讲完了。 下面附上数据绑定相关部分精简代码实现的完整版：

// Observer

	/**
	 * Observer会将数据对象的所有属性都转换为getter/setter的形式来收集依赖（前面我们说的订阅者），并当属性值变化时通知这些依赖
	 */

	export default class Observer() {
		constructor(value) {
			this.value = value;
			if (!Array.isArray(value)) {
				this.walk(value);
			}
			// 注意数组的处理方式略有不同，这里不暂且不包括数组的处理
		}

		walk(obj) {
			const keys = Objects.keys(obj);
			for(let i=0, l = keys.length; i<l; i++) {
				defineReactive(obj, keys[i], obj[keys[i]])
			}
		}
	}


	// defineReactive
	function defineReactive(data, key, val) {
		// 递归子属性
		if (typeof val === 'object') {
			new Observer(val);
		}
		let dep = new Dep();
		Object.defineProperty(data, key, {
			enumerable: true,
			configulable: true,
			get() {
				dep.depend();
				return val;
			},
			set(newVal) {
				if (val === newVal) {
					return;
				}
				val = newVal;
				dep.notify();
			}
		})
	}


// Dep

		export default class Dep {
			constructor() {
				this.subs = [];
			}
			addSub(sub) {
				this.subs.push(sub);
			}
			removeSub(sub) {
				remove(this,subs, sub);
			}
			depend() {
				if (Dep.target) {
					this.addSub(Dep.target);
				}
			}
			notify() {
				const subs = this.subs.slices();
				for (let i=0,l=subs.length; i<l; i++) {
					subs[i].updates();
				}
			}
		}

		function remove(arr, item) {
			if (arr && arr.length) {
				const index = arr.indexOf(item);
				if (index > -1) {
					return arr.splice(index, 1)
				}
			}
		}

// Watcher

		export default class Watcher {
			constructor(vm, expOrFn, cb) {
				this.vm = vm;
				this.getter = parsePath(expOrFn);
				this.cb = cb;
				this.value = this.get();
			}
			get() {
				Dep.target = this;
				let value = this.getter.call(this.vm, this.vm);
				Dep.target = null;
				return value;
			}
			update() {
				const oldValue= this.value;
				this.value = this.get();
				this.cb.call(this.vm, this.value, oldValue);
			}
		}

		const bailRe = /[^λw.$]/
		function parsePath(path) {
			if (bailRe.test(path)) {
				return;
			}
			const segments = path.split('.');
			return function (obj) {
				for (let i=0, l=segments.length; i < l; i++) {
					if (!obj) return;
					obj = obj[segments[i]];
				}
				return obj;
			}
		}


